import React, { Component } from 'react';
import '../about/style.css';
import { Container, Row } from 'react-bootstrap';
import Footer from '../../components/footer';
import Animate from 'react-smooth';
import ReactGa from 'react-ga';
import { Helmet } from 'react-helmet';
import CV from '../../components/cv';
import HamburgerMenu from '../../components/hamburger_menu';
import particles from '../../const/about_particle.js';
import Particles from 'react-particles-js';

class About extends Component {

    componentDidMount() {
        //--daniel davids Portfolio website Google analytics--
        //--Replace with your own key !!
        ReactGa.initialize("UA-154721739-1");
        ReactGa.pageview('motasimfoad.com - About Screen');
        //--daniel davids Portfolio website Google analytics--
    }

    render() {
        return ( <
            Container className = "About-header"
            fluid = { true } >
            <
            Helmet >
            <
            meta charSet = "utf-8" / >
            <
            title > About | daniel david < /title> <
            link rel = "canonical"
            href = "http://motasimfoad.com/about" / >
            <
            meta name = "description"
            content = "daniel david | About - Product Manager | Project Manager | Software Engineer
            Experienced Project & Product Manager with a demonstrated history of working in computer software industry.Skilled in leading edge project management, web & software development tools & trends.Strong computer engineering base with a BSc in Computer Science focused in Computer Software Engineering from BRAC University.In addition to that minor on Basic Business studies to assist me through product & project management " /> <
            /Helmet> <
            Animate to = "1"
            from = "0"
            attributeName = "opacity" >
            <
            Particles params = { particles }
            className = "particle" /
            >
            <
            Row className = "About-main" >
            <
            HamburgerMenu / >
            <
            CV / >
            <
            /Row> <
            Row className = "About-footer" >
            <
            Footer / >
            <
            /Row> <
            /Animate> <
            /Container>
        );
    }
}

export default About;
